import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/navbar';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import GetCustomer from './components/customer/GetCustomer';
import UpdateCustomer from './components/customer/UpdateCustomer';
import DeleteCustomer from './components/customer/DeleteCustomer';
import PaymentPage from './components/payment/Paymentpage';
import CreatePayment from './components/payment/CreatePayment';
import EmployeeDashboard from './components/employee/EmployeeDashboard';
import EmployeePayments from './components/employee/EmployeePayments';
import './App.css';

function ProtectedRoute({ children }) {
    const isAuthenticated = !!localStorage.getItem('token');
    return isAuthenticated ? children : <Navigate to="/login" />;
}

function App() {
    return (
        <Router>
            <div className="App">
                <Navbar />
                <Routes>
                    <Route path="/" element={<Navigate to="/login" />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/register" element={<Register />} />

                    {/* Customer Routes */}
                    <Route path="/customers" element={
                        <ProtectedRoute>
                            <GetCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/update/:id" element={
                        <ProtectedRoute>
                            <UpdateCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/delete/:id" element={
                        <ProtectedRoute>
                            <DeleteCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/payment" element={
                        <ProtectedRoute>
                            <PaymentPage />
                        </ProtectedRoute>
                    } />
                    <Route path="/create" element={
                        <ProtectedRoute>
                            <CreatePayment />
                        </ProtectedRoute>
                    } />

                    {/* Employee Routes */}
                    <Route path="/employee/dashboard" element={
                        <ProtectedRoute>
                            <EmployeeDashboard />
                        </ProtectedRoute>
                    } />
                    <Route path="/employee/payments" element={
                        <ProtectedRoute>
                            <EmployeePayments />
                        </ProtectedRoute>
                    } />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
