import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import './navbar.css';

const Navbar = () => {
    const isAuthenticated = !!localStorage.getItem('token');
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.clear(); // Clear all localStorage items
        navigate('/login');   // Redirect to login page
    };

    return (
        <nav className="navbar">
            <ul className="navbar-list">
                {!isAuthenticated && (
                    <>
                        <li>
                            <NavLink to="/register" className="navbar-link">Register</NavLink>
                        </li>
                        <li>
                            <NavLink to="/login" className="navbar-link">Login</NavLink>
                        </li>
                    </>
                )}
                {isAuthenticated && (
                    <>
                        <li>
                            <NavLink to="/customers" className="navbar-link">Customers</NavLink>
                        </li>
                        <li>
                            <NavLink to="/payment" className="navbar-link">Payment Page</NavLink>
                        </li>
                        <li>
                            <button onClick={handleLogout} className="navbar-link logout-button">Logout</button>
                        </li>
                    </>
                )}
            </ul>
        </nav>
    );
};

export default Navbar;
