import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink, useLocation, useNavigate } from "react-router-dom";
import './Paymentpage.css';

function PaymentPage() {
    const [payments, setPayments] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        // Check if we have a newly created payment from the CreatePayment page
        if (location.state?.newPayment) {
            setPayments([location.state.newPayment]); // Only display the new payment
        } else {
            // Fetch all payments if no new payment is passed in state
            const fetchPayments = async () => {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Authentication required.');
                    navigate('/login');
                    return;
                }

                try {
                    const response = await axios.get('https://localhost:5000/api/payments', {
                        headers: {
                            Authorization: `Bearer ${token}`,
                        },
                    });

                    if (response.status === 200) {
                        setPayments(response.data);
                    } else {
                        setError('Failed to fetch payments. Please try again later.');
                    }
                } catch (err) {
                    setError('Error fetching payments. Please try again later.');
                }
            };
            fetchPayments();
        }
    }, [location.state, navigate]);

    return (
        <div>
            <h1>Payment Page</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <NavLink to="/create">
                <button>Create New Payment</button>
            </NavLink>
            <h2>Payment List</h2>
            {payments.length > 0 ? (
                <ul className="payment-list">
                    {payments.map(payment => (
                        <li key={payment._id} className="payment-list-item">
                            <p><strong>Customer ID:</strong> {payment.customerID}</p>
                            <p><strong>Amount:</strong> {payment.amount}</p>
                            <p><strong>Currency:</strong> {payment.currency}</p>
                            <p><strong>Recipient Account Number:</strong> {payment.recipientAccountNumber}</p>
                            <p><strong>SWIFT Code:</strong> {payment.SWIFTCode}</p>
                            <p><strong>Status:</strong> {payment.status}</p>
                            <p><strong>Created At:</strong> {new Date(payment.createdAt).toLocaleString()}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No payments available.</p>
            )}
        </div>
    );
}

export default PaymentPage;
