import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Paymentpage from './components/payment/Paymentpage';
import Navbar from './components/navbar';
import GetCustomer from './components/customer/GetCustomer';
import UpdateCustomer from './components/customer/UpdateCustomer';
import DeleteCustomer from './components/customer/DeleteCustomer';
import CreatePayment from './components/payment/CreatePayment';
import EmployeeDashboard from './components/employee/EmployeeDashboard';
import EmployeePayments from './components/employee/EmployeePayments';
import './App.css';

// Protected Route Component for Role-Based Access
function ProtectedRoute({ children, requiredRole }) {
    const isAuthenticated = !!localStorage.getItem('token');
    const userRole = localStorage.getItem('role');

    if (!isAuthenticated) {
        return <Navigate to="/login" />;
    }
    if (requiredRole && userRole !== requiredRole) {
        return <Navigate to="/" />;
    }
    return children;
}

function App() {
    return (
        <Router>
            <div className='App'>
                <Navbar />
                <Routes>
                    {/* Public Routes */}
                    <Route path="/" element={<Navigate to="/login" />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/login" element={<Login />} />

                    {/* Customer Protected Routes */}
                    <Route path="/customers" element={
                        <ProtectedRoute requiredRole="customer">
                            <GetCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/update/:id" element={
                        <ProtectedRoute requiredRole="customer">
                            <UpdateCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/delete/:id" element={
                        <ProtectedRoute requiredRole="customer">
                            <DeleteCustomer />
                        </ProtectedRoute>
                    } />
                    <Route path="/payment" element={
                        <ProtectedRoute requiredRole="customer">
                            <Paymentpage />
                        </ProtectedRoute>
                    } />
                    <Route path="/create" element={
                        <ProtectedRoute requiredRole="customer">
                            <CreatePayment />
                        </ProtectedRoute>
                    } />

                    {/* Employee Protected Routes */}
                    <Route path="/employee/dashboard" element={
                        <ProtectedRoute requiredRole="employee">
                            <EmployeeDashboard />
                        </ProtectedRoute>
                    } />
                    <Route path="/employee/payments" element={
                        <ProtectedRoute requiredRole="employee">
                            <EmployeePayments />
                        </ProtectedRoute>
                    } />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
