import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function PaymentPage() {
    const [payments, setPayments] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchPayments = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/payments'); // Adjust URL if needed
                setPayments(response.data);
            } catch (err) {
                setError('Error fetching payments');
            }
        };
        fetchPayments();
    }, []);

    return (
        <div>
            <h1>Payment Page</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {payments.map((payment) => (
                    <li key={payment._id}>
                        <p><strong>Customer:</strong> {payment.customerID?.fullName || 'N/A'}</p>
                        <p><strong>Amount:</strong> {payment.amount}</p>
                        <p><strong>Currency:</strong> {payment.currency}</p>
                        <p><strong>Recipient Account Number:</strong> {payment.recipientAccountNumber}</p>
                        <p><strong>SWIFT Code:</strong> {payment.SWIFTCode}</p>
                        <p><strong>Status:</strong> {payment.status}</p>
                    </li>
                ))}
            </ul>
            <Link to="/create-payment">Create More Payments</Link> {/* Link to create payment page */}
        </div>
    );
}

export default PaymentPage;
