import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';

function Register() {
    const [fullName, setFullName] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [IDNumber, setIDNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post('http://localhost:5000/api/auth/register', {
                fullName,
                username,
                email,
                IDNumber,
                accountNumber,
                password,
            });

            if (response.status === 201) {
                // Log response to confirm the presence of the ObjectID
                console.log('Registration response:', response.data);

                // Extract ObjectID from the response data
                const { customer } = response.data; // Assuming the response contains customer object with ObjectID

                // Store ObjectID and account number in localStorage
                localStorage.setItem('customerID', customer._id); // Store ObjectID as customerID
                localStorage.setItem('accountNumber', accountNumber); // Store account number
                navigate('/'); // Redirect to home or dashboard
            }
        } catch (err) {
            if (err.response) {
                setError(err.response.data.message);
            } else {
                setError('Something went wrong. Please try again.');
            }
        }
    };

    return (
        <div className="container">
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                {/* Input fields */}
                <label htmlFor="fullName">Full Name</label>
                <input type="text" id="fullName" value={fullName} onChange={e => setFullName(e.target.value)} required />
                <label htmlFor="username">Username</label>
                <input type="text" id="username" value={username} onChange={e => setUsername(e.target.value)} required />
                <label htmlFor="email">Email</label>
                <input type="email" id="email" value={email} onChange={e => setEmail(e.target.value)} required />
                <label htmlFor="IDNumber">ID Number</label>
                <input type="text" id="IDNumber" value={IDNumber} onChange={e => setIDNumber(e.target.value)} required />
                <label htmlFor="accountNumber">Account Number</label>
                <input type="text" id="accountNumber" value={accountNumber} onChange={e => setAccountNumber(e.target.value)} required />
                <label htmlFor="password">Password</label>
                <input type="password" id="password" value={password} onChange={e => setPassword(e.target.value)} required />
                {error && <p className="error">{error}</p>}
                <button type="submit">Register</button>
            </form>
        </div>
    );
}

export default Register;
