import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Paymentpage from './components/payment/Paymentpage';
import Navbar from './components/navbar';
import GetCustomer from './components/customer/GetCustomer';
import UpdateCustomer from './components/customer/UpdateCustomer';
import DeleteCustomer from './components/customer/DeleteCustomer';
import CreatePayment from './components/payment/CreatePayment';
import './App.css';

function RequireAuth({ children }) {
    const isAuthenticated = !!localStorage.getItem('token');
    return isAuthenticated ? children : <Navigate to="/login" />;
}

function App() {
    return (
        <Router>
            <div className='App'>
                <Navbar />
                <Routes>
                    <Route path="/" element={<Navigate to="/login" />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/customers" element={<RequireAuth><GetCustomer /></RequireAuth>} />
                    <Route path="/update/:id" element={<RequireAuth><UpdateCustomer /></RequireAuth>} />
                    <Route path="/delete/:id" element={<RequireAuth><DeleteCustomer /></RequireAuth>} />
                    <Route path="/payment" element={<RequireAuth><Paymentpage /></RequireAuth>} />
                    <Route path="/create" element={<RequireAuth><CreatePayment /></RequireAuth>} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
