import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';

function Register() {
    const [fullName, setFullName] = useState('');
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [IDNumber, setIDNumber] = useState(''); // User-provided IDNumber
    const [accountNumber, setAccountNumber] = useState(''); // User-provided account number
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload
        setError(''); // Reset error state

        try {
            // Send registration data to the server
            const response = await axios.post('http://localhost:5000/api/auth/register', {
                fullName,
                username,
                email,
                IDNumber,
                accountNumber,
                password,
            });

            // If registration is successful
            if (response.status === 201) {
                // Log the server response to confirm the presence of the customer object with MongoDB ObjectID
                console.log('Registration response:', response.data);

                // Extract customer data, specifically the generated MongoDB ObjectID
                const { customer } = response.data; // Assuming the response contains a customer object

                // Store MongoDB generated ObjectID and account number in localStorage for later use
                localStorage.setItem('customerID', customer._id); // Store MongoDB ObjectID as customerID
                localStorage.setItem('accountNumber', accountNumber); // Store account number in localStorage

                // Redirect to home or dashboard
                navigate('/');
            }
        } catch (err) {
            if (err.response && err.response.data.message) {
                // Display the error message returned by the server
                setError(err.response.data.message);
            } else {
                // Handle unexpected errors
                setError('Something went wrong. Please try again.');
            }
        }
    };

    return (
        <div className="container">
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                {/* Input fields */}
                <label htmlFor="fullName">Full Name</label>
                <input
                    type="text"
                    id="fullName"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    required
                />

                <label htmlFor="username">Username</label>
                <input
                    type="text"
                    id="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />

                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <label htmlFor="IDNumber">ID Number</label>
                <input
                    type="text"
                    id="IDNumber"
                    value={IDNumber}
                    onChange={(e) => setIDNumber(e.target.value)}
                    required
                />

                <label htmlFor="accountNumber">Account Number</label>
                <input
                    type="text"
                    id="accountNumber"
                    value={accountNumber}
                    onChange={(e) => setAccountNumber(e.target.value)}
                    required
                />

                <label htmlFor="password">Password</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                {/* Error message */}
                {error && <p className="error">{error}</p>}

                <button type="submit">Register</button>
            </form>
        </div>
    );
}

export default Register;
