import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreatePayment() {
    const [customerID, setCustomerID] = useState('');
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('');
    const [recipientAccountNumber, setRecipientAccountNumber] = useState('');
    const [SWIFTCode, setSWIFTCode] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch customer data from local storage
        const storedCustomerID = localStorage.getItem('customerID');
        const storedAccountNumber = localStorage.getItem('accountNumber');
    
        // Set the state with stored values
        if (storedCustomerID) {
            setCustomerID(storedCustomerID);
        }
        if (storedAccountNumber) {
            setRecipientAccountNumber(storedAccountNumber); // This will auto-complete the recipient account number
        }
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault(); // Prevent page reload

        // Get the token from local storage
        const token = localStorage.getItem('token');

        try {
            const response = await axios.post('http://localhost:5000/api/create', {
                customerID,
                amount,
                currency,
                recipientAccountNumber,
                SWIFTCode
            }, {
                headers: {
                    Authorization: `Bearer ${token}` // Set the Authorization header
                }
            });

            setSuccess(response.data.message);
            setError('');

            // Optionally redirect to the payments page after successful creation
            navigate('/payments');
        } catch (err) {
            setError(err.response?.data?.message || 'Error creating payment');
            setSuccess('');
        }
    };

    return (
        <div>
            <h1>Create New Payment</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Customer ID:</label>
                    <input 
                        type="text" 
                        value={customerID} 
                        onChange={(e) => setCustomerID(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Amount:</label>
                    <input 
                        type="number" 
                        value={amount} 
                        onChange={(e) => setAmount(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Currency:</label>
                    <input 
                        type="text" 
                        value={currency} 
                        onChange={(e) => setCurrency(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Recipient Account Number:</label>
                    <input 
                        type="text" 
                        value={recipientAccountNumber} 
                        onChange={(e) => setRecipientAccountNumber(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>SWIFT Code:</label>
                    <input 
                        type="text" 
                        value={SWIFTCode} 
                        onChange={(e) => setSWIFTCode(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">Create Payment</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
        </div>
    );
}

export default CreatePayment;
