import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        const usernameRegex = /^[a-zA-Z0-9_]{3,30}$/;
        const passwordRegex = /^[A-Za-z0-9!@#$%^&*()_+=-]{6,30}$/;

        if (!usernameRegex.test(username) || !passwordRegex.test(password)) {
            setError("Invalid username or password format");
            return;
        }

        try {
            const response = await axios.post('https://localhost:5000/api/auth/login', { username, password });
            const { token, user } = response.data;

            localStorage.setItem('token', token);
            localStorage.setItem('IDNumber', user.IDNumber || '');
            localStorage.setItem('accountNumber', user.accountNumber || '');
            localStorage.setItem('userID', user.userID);
            localStorage.setItem('username', username);
            localStorage.setItem('role', user.role); // Store role in local storage

            if (user.role === 'customer') {
                navigate("/customers");
            } else if (user.role === 'employee') {
                navigate("/employee/dashboard");
            }
        } catch (err) {
            if (err.response) {
                setError(err.response.data.message);
            } else {
                setError('Something went wrong. Please try again.');
            }
        }
    };

    return (
        <div className="container">
            <h1>Login Page</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username:</label>
                <input
                    type='text'
                    id='username'
                    name='username'
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    autoComplete='true'
                />
                <label htmlFor="password">Password:</label>
                <input
                    type='password'
                    id='password'
                    name='password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {error && <p className="error">{error}</p>}
                <button type='submit'>Login</button>
            </form>
        </div>
    );
}

export default Login;
