import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import './GetCustomer.css'; // Import the CSS file

function GetCustomer() {
    const [customers, setCustomers] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchCustomers = async () => {
            try {
                const token = localStorage.getItem('token');

                const response = await axios.get('http://localhost:5000/api/', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setCustomers(response.data);
            } catch (err) {
                setError('You are not authorized to view the customers');
            }
        };
        fetchCustomers();
    }, []);

    return (
        <div className="container"> {/* Add container class */}
            <h1>Get Customers</h1>
            {error ? (
                <p className="error">{error}</p>
            ) : customers.length > 0 ? (
                <ul className="customer-list"> {/* Add customer list class */}
                    {customers.map(customer => (
                        <li key={customer._id} className="customer-item"> {/* Add customer item class */}
                            <div className="customer-info"> {/* Add customer info class */}
                                <strong>Full Name:</strong> {customer.fullName} <br />
                                <strong>Username:</strong> {customer.username} <br />
                                <strong>Email:</strong> {customer.email} <br />
                                <strong>ID Number:</strong> {customer.IDNumber.slice(0, 6)}******* <br />
                                <strong>Account Number:</strong> ****{customer.accountNumber.slice(-4)} <br />
                                <strong>Password:</strong> ********
                            </div>
                            <div> {/* Action links container */}
                                <NavLink to={`/update/${customer._id}`} className="link">Update</NavLink>
                                <NavLink to={`/delete/${customer._id}`} className="link">Delete</NavLink>
                            </div>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default GetCustomer;
