import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Paymentpage from './components/payment/Paymentpage';
import Navbar from './components/navbar';
import GetCustomer from './components/customer/GetCustomer';
import UpdateCustomer from './components/customer/UpdateCustomer';
import DeleteCustomer from './components/customer/DeleteCustomer';
import CreatePayment from './components/payment/CreatePayment';
import './App.css';

function App() {
  const isAuthenticated = () => !!localStorage.getItem('token');

  return (
    <Router>
      <div className='App'>
        <Navbar />
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="/customers" element={isAuthenticated() ? <GetCustomer /> : <Navigate to="/login" />} />
          <Route path="/update/:id" element={isAuthenticated() ? <UpdateCustomer /> : <Navigate to="/login" />} />
          <Route path="/delete/:id" element={isAuthenticated() ? <DeleteCustomer /> : <Navigate to="/login" />} />
          <Route path="/payment" element={isAuthenticated() ? <Paymentpage /> : <Navigate to="/login" />} />
          <Route path="/create" element={isAuthenticated() ? <CreatePayment /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
