import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CreatePayment.css';

function CreatePayment() {
    const [customerID, setCustomerID] = useState('');
    const [IDNumber, setIDNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [amount, setAmount] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const storedIDNumber = localStorage.getItem('IDNumber');
        const storedAccountNumber = localStorage.getItem('accountNumber');
        const storedCustomerID = localStorage.getItem('customerID');

        if (storedIDNumber && storedAccountNumber && storedCustomerID) {
            setIDNumber(storedIDNumber);
            setAccountNumber(storedAccountNumber);
            setCustomerID(storedCustomerID);
        } else {
            setError('User data not found. Please log in again.');
            navigate('/login');
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (isNaN(amount) || amount <= 0) {
            setError("Please enter a valid amount.");
            return;
        }

        const token = localStorage.getItem('token');
        if (!token) {
            setError('Authentication required. Please log in again.');
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post('https://localhost:5000/api/payment/create', {
                customerID,
                amount: parseFloat(amount),
                currency: 'ZAR',
                recipientAccountNumber: accountNumber,
                SWIFTCode: 'NEDSZAJJXXX'
            }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (response.status === 201) {
                setSuccess('Payment created successfully!');
                navigate('/payment');
            } else {
                setError('Payment creation failed.');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Error creating payment');
        }
    };

    return (
        <div>
            <h1>Create New Payment</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Customer ID:</label>
                    <input type="text" value={customerID} readOnly />
                </div>
                <div>
                    <label>ID Number:</label>
                    <input type="text" value={IDNumber} readOnly />
                </div>
                <div>
                    <label>Account Number:</label>
                    <input type="text" value={accountNumber} readOnly />
                </div>
                <div>
                    <label>Amount:</label>
                    <input 
                        type="number" 
                        value={amount} 
                        onChange={(e) => setAmount(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">Create Payment</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
        </div>
    );
}

export default CreatePayment;
