import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault(); // Prevent page reload

        try {
            const response = await axios.post('http://localhost:5000/api/login', {
                username,
                password
            });
            
            // Assuming response contains customer data
            const customer = response.data.customer; // Adjust this based on your response structure

            // Store customerID and accountNumber in local storage
            localStorage.setItem('customerID', customer.customerID); // Replace with actual customer ID key
            localStorage.setItem('accountNumber', customer.accountNumber); // Replace with actual account number key

            // Redirect or perform other actions after successful login
            navigate('/payment'); // Navigate to the payment page or wherever appropriate
        } catch (error) {
            console.error(error);
            setError('Invalid username or password'); // Handle login error
        }
    };

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Username:</label>
                    <input 
                        type="text" 
                        value={username} 
                        onChange={(e) => setUsername(e.target.value)} 
                        required 
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        required 
                    />
                </div>
                <button type="submit">Login</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default Login;
