import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeePayments.css';

function EmployeePayments() {
    const [payments, setPayments] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchPayments = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('Authentication required.');
                return;
            }

            try {
                const response = await axios.get('https://localhost:5000/api/payments', {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setPayments(response.data);
            } catch (err) {
                setError('Failed to fetch payments.');
            }
        };

        fetchPayments();
    }, []);

    const handleStatusChange = async (paymentId, status) => {
        const token = localStorage.getItem('token');
        try {
            await axios.put(
                `https://localhost:5000/api/payments/${paymentId}`,
                { status },
                {
                    headers: { Authorization: `Bearer ${token}` }
                }
            );
            setPayments(prevPayments => prevPayments.filter(payment => payment._id !== paymentId));
        } catch (err) {
            setError('Failed to update payment status.');
        }
    };

    return (
        <div>
            <h1>Employee Payments</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {payments.map(payment => (
                    <li key={payment._id} className="employee-payment-item">
                        <p>Customer ID: {payment.customerID}</p>
                        <p>Amount: {payment.amount}</p>
                        <p>Status: {payment.status}</p>
                        <p>Date: {new Date(payment.createdAt).toLocaleString()}</p>
                        <button
                            className="accept-button"
                            onClick={() => handleStatusChange(payment._id, 'Verified')}
                        >
                            Accept
                        </button>
                        <button
                            className="reject-button"
                            onClick={() => handleStatusChange(payment._id, 'Rejected')}
                        >
                            Reject
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default EmployeePayments;
