import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './CreatePayment.css';

function CreatePayment() {
    const [customerID, setCustomerID] = useState('');
    const [IDNumber, setIDNumber] = useState('');
    const [amount, setAmount] = useState('');
    const [currency] = useState('ZAR');
    const [recipientAccountNumber, setRecipientAccountNumber] = useState('');
    const [SWIFTCode] = useState('NEDSZAJJXXX');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const storedIDNumber = localStorage.getItem('IDNumber');
        const storedAccountNumber = localStorage.getItem('accountNumber');
        const storedCustomerID = localStorage.getItem('customerID');

        if (storedIDNumber && storedAccountNumber && storedCustomerID) {
            setIDNumber(storedIDNumber);
            setRecipientAccountNumber(storedAccountNumber);
            setCustomerID(storedCustomerID);
        } else {
            setError('User data not found. Please log in again.');
            navigate('/login');
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!customerID || !amount || !currency || !recipientAccountNumber || !SWIFTCode) {
            setError('Please fill out all fields.');
            return;
        }

        const token = localStorage.getItem('token');
        if (!token) {
            setError('Authentication required. Please log in again.');
            navigate('/login');
            return;
        }

        try {
            const response = await axios.post('https://localhost:5000/api/payment/create', {
                customerID,
                amount: parseFloat(amount),
                currency,
                recipientAccountNumber,
                SWIFTCode
            }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            if (response.status === 201) {
                setSuccess('Payment created successfully!');
                setError('');
                navigate('/payment', { state: { newPayment: response.data.payment } });
            } else {
                setError('Payment creation failed.');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Error creating payment');
        }
    };

    return (
        <div>
            <h1>Create New Payment</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Customer ID (ID Number):</label>
                    <input type="text" value={IDNumber} readOnly />
                </div>
                <div>
                    <label>Amount:</label>
                    <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} required />
                </div>
                <div>
                    <label>Currency:</label>
                    <input type="text" value={currency} readOnly />
                </div>
                <div>
                    <label>Recipient Account Number:</label>
                    <input type="text" value={recipientAccountNumber} readOnly />
                </div>
                <div>
                    <label>SWIFT Code:</label>
                    <input type="text" value={SWIFTCode} readOnly />
                </div>
                <button type="submit">Create Payment</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
        </div>
    );
}

export default CreatePayment;
