import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Navbar from './components/navbar';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import GetCustomer from './components/customer/GetCustomer';
import UpdateCustomer from './components/customer/UpdateCustomer';
import DeleteCustomer from './components/customer/DeleteCustomer';
import PaymentPage from './components/payment/Paymentpage';
import CreatePayment from './components/payment/CreatePayment';
import EmployeeDashboard from './components/employee/EmployeeDashboard';
import EmployeePayments from './components/employee/EmployeePayments';
import './App.css';

function App() {
    const navigate = useNavigate();

    useEffect(() => {
        // Check for token on app load to validate session
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
        }
    }, [navigate]);

    return (
        <div className="App">
            <Navbar />
            <Routes>
                <Route path="/" element={<Navigate to="/login" />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />

                {/* Customer Routes */}
                <Route path="/customers" element={<GetCustomer />} />
                <Route path="/update/:id" element={<UpdateCustomer />} />
                <Route path="/delete/:id" element={<DeleteCustomer />} />
                <Route path="/payment" element={<PaymentPage />} />
                <Route path="/create" element={<CreatePayment />} />

                {/* Employee Routes */}
                <Route path="/employee/dashboard" element={<EmployeeDashboard />} />
                <Route path="/employee/payments" element={<EmployeePayments />} />
            </Routes>
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>
            <App />
        </Router>
    );
}
