import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink, useLocation } from "react-router-dom";
import './Paymentpage.css';

function PaymentPage() {
    const [payments, setPayments] = useState([]);
    const [error, setError] = useState('');
    const location = useLocation();

    // Fetch payments from the server
    const fetchPayments = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('https://localhost:5000/api/payments', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setPayments(response.data);
        } catch (err) {
            setError('Error fetching payments');
        }
    };

    useEffect(() => {
        fetchPayments();  // Fetch on mount
    }, []);

    useEffect(() => {
        // Check if there's a new payment in location state and add it if unique
        if (location.state && location.state.newPayment) {
            const newPayment = location.state.newPayment;
            console.log("Checking for duplication. New Payment ID:", newPayment._id);

            const alreadyExists = payments.some(payment => payment._id === newPayment._id);

            if (!alreadyExists) {
                console.log("Adding new payment:", newPayment);
                setPayments(prevPayments => [...prevPayments, newPayment]);

                // Clear the state after adding the new payment
                window.history.replaceState({}, document.title, location.pathname);
            } else {
                console.log("Duplicate payment detected, not adding.");
            }
        }
    }, [location.state, payments, location.pathname]);

    return (
        <div>
            <h1>Payment Page</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <NavLink to="/create">
                <button>Create New Payment</button>
            </NavLink>
            <h2>Payment List</h2>
            {payments.length > 0 ? (
                <ul className="payment-list">
                    {payments.map(payment => (
                        <li key={payment._id} className="payment-list-item">
                            <p><strong>Customer ID:</strong> {payment.customerID}</p>
                            <p><strong>Amount:</strong> {payment.amount}</p>
                            <p><strong>Currency:</strong> {payment.currency}</p>
                            <p><strong>Recipient Account Number:</strong> {payment.recipientAccountNumber}</p>
                            <p><strong>SWIFT Code:</strong> {payment.SWIFTCode}</p>
                            <p><strong>Status:</strong> {payment.status}</p>
                            <p><strong>Created At:</strong> {new Date(payment.createdAt).toLocaleString()}</p>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No payments available.</p>
            )}
        </div>
    );
}

export default PaymentPage;
