import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function UpdateCustomer() {
    const { id } = useParams();
    const navigate = useNavigate();

    const [customer, setCustomer] = useState({
        fullName: '',
        username: '',
        email: '',
        IDNumber: '',
        accountNumber: '',
        password: ''
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [showConfirmation, setShowConfirmation] = useState(false);

    // Fetch customer data when component loads
    useEffect(() => {
        const fetchCustomer = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5000/api/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setCustomer(response.data);
            } catch (err) {
                setError('Unable to fetch customer details.');
            }
        };
        fetchCustomer();
    }, [id]);

    const handleChange = (e) => {
        setCustomer({ ...customer, [e.target.name]: e.target.value });
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        // Show confirmation message instead of directly submitting
        setShowConfirmation(true);
    };

    const handleUpdate = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.put(`http://localhost:5000/api/${id}`, customer, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setSuccess('Customer updated successfully!');
            navigate("/customers"); // Redirect to the Get Customers page
        } catch (err) {
            setError('Failed to update customer details.');
        }
    };

    const handleCancel = () => {
        // Navigate back to the Get Customers page without saving
        navigate("/customers");
    };

    return (
        <div>
            <h1>Update Customer</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}

            {!showConfirmation ? (
                <form onSubmit={handleFormSubmit}>
                    <label>
                        Full Name:
                        <input
                            type="text"
                            name="fullName"
                            value={customer.fullName}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <label>
                        Username:
                        <input
                            type="text"
                            name="username"
                            value={customer.username}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <label>
                        Email:
                        <input
                            type="email"
                            name="email"
                            value={customer.email}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <label>
                        ID Number:
                        <input
                            type="text"
                            name="IDNumber"
                            value={customer.IDNumber}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <label>
                        Account Number:
                        <input
                            type="text"
                            name="accountNumber"
                            value={customer.accountNumber}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <label>
                        Password:
                        <input
                            type="password"
                            name="password"
                            value={customer.password}
                            onChange={handleChange}
                        />
                    </label>
                    <br />

                    <button type="submit">Submit</button>
                </form>
            ) : (
                <div>
                    <p>Are you sure you want to update this information?</p>
                    <button onClick={handleUpdate}>Update</button>
                    <button onClick={handleCancel}>Cancel</button>
                </div>
            )}
        </div>
    );
}

export default UpdateCustomer;
