import React, {useEffect,useState} from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

function GetCustomer(){
    const[customers, setCustomers] = useState([]);
    const[error, setError] = useState('');

    useEffect( () => {
        const fetchCustomers = async () => {
            try {
                const token = localStorage.getItem('token');

                const response = await axios.get('http://localhost:5000/api/', {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setCustomers(response.data);
            }catch (err) {
                setError('You are not authorized to view the customers');
            }
        };
        fetchCustomers();
    }, []);
    return <div>
            <h1> Get Customers</h1>
            <NavLink to="/register">Add new Customer</NavLink>
            {error ? <p style={{color: 'red'}}>
                {error}
            </p> : customers.length > 0 ? <ul>
                {customers.map(customer => <li key={customer._id}>
                    <p><strong>Full Name:</strong> {customer.fullName}</p>
                    <p><strong>Username:</strong> {customer.username}</p>
                    <p><strong>Email:</strong> {customer.email}</p>
                    <p><strong>ID Number:</strong> {customer.IDNumber.slice(0, 6)}*******</p> 
                    <p><strong>Account Number:</strong> ****{customer.accountNumber.slice(-4)}</p> 
                    <p><strong>Password:</strong> ********</p> 
                    <NavLink to={`/edit/${customer.id}`}>Edit</NavLink>
                    <NavLink to={`/update/${customer.id}`}>Update</NavLink>
                    <NavLink to={`/delete/${customer.id}`}>Delete</NavLink>
                </li>)}
            </ul>: <p>Loading...</p>}
        </div>
    
};

export default GetCustomer;
